import React, { useEffect, useState } from 'react';

const News = () => {
    const [news,setNews]=useState([])
    useEffect(()=>{
        var url="https://jsonplaceholder.typicode.com/posts/1";
        fetch(url)
        .then(response => response.json())
        .then(json => setNews(json))
    })
    console.log(news);
    return (
        <div>
            <h3>Total Article : {news.length}</h3>
        </div>
    );
};

export default News;
